          //fetch post to lecture_schedules

          // id: 1,
          // event: "Mod1 Ruby fundamental",
          // date: "2019-01-22",
          // day: "Tuesday",
          // start_time: "2000-01-01T09:30:00.000Z",
          // end_time: "2000-01-01T11:00:00.000Z",
          // mod_id: 1,
          // lecture_room_id: 1

    }

    componentDidMount(){
      fetch('http://localhost:3000/api/v1/lecture_rooms')
        .then(res=>res.json())
        .then(schedules=>this.setState({schedules}))
    }


  render() {
    return (
      <div className="App">
        <header>
          <div id="logo">
            <span className="icon swing">date_range</span>
            <span>
              Book <b>Lecture Room</b>
            </span>
          </div>
        </header>

          <SchedulePageContainer 
              currentMonth={this.state.currentMonth}
              selectedDate={this.state.selectedDate}
              nextMonth={this.nextMonth}
              prevMonth={this.prevMonth}
              onDateClick={this.onDateClick}
              schedules={this.state.schedules}
              onBookItHandler={this.onSubmitFormHandler}
          />


      </div>
    );
  }
}

export default App;
