import React, {Component} from 'react';


class BookingForm extends Component{

    state={
        startTime:360
    }

    populateSelectBox=(min) => {
        let hours, minutes, ampm;
            hours = Math.floor(min / 60);
            minutes = min % 60;
            if (minutes < 10){
                minutes = '0' + minutes; // adding leading zero
            }
            ampm = hours % 24 < 12 ? 'AM' : 'PM';
            hours = hours % 12;
            if (hours === 0){
                hours = 12;
            }

            return hours+':'+minutes+' '+ampm;
    }

    range=() => {
        let array=[];
        let startingMin=this.state.startTime+15
        for(let i = startingMin; i <= 1320; i += 15){
            array.push(i);
        }
        return array;
    }

    render(){
        return ( 
            <form onSubmit={(e)=>{this.props.onBookItHandler(e, this.props.lectureRoomName);this.props.toggleBooking();}}>
                <label>Start time</label>
                <select name='start_time' onChange={(e)=>{this.props.onChangeBookForm(e);this.setState({startTime:e.target.value})}}>
                    <option>From</option>
                    {this.range().map(minute=><option key={minute}>
                        {this.populateSelectBox(minute)}
                    </option>)}
                </select>

                <label>End time</label>
                <select name='end_time' onChange={(e)=>this.props.onChangeBookForm(e)}>
                    <option>To</option>
                    {this.range().map(minute=><option key={minute}>
                        {this.populateSelectBox(minute)}
                    </option>)}
                </select>

                <label>Event Name</label>
                <input type='text' name='event' placeholder='enter event name' value={this.props.event}
                    onChange={(e)=>this.props.onChangeBookForm(e)}></input>
                <input type='submit' value='Book It!'/>
                <button onClick={this.props.onCancelBookHandler}>Cancel</button>
            </form>
        );
    }
}

export default BookingForm;
