import React, {Component} from 'react';
import Schedule from './Schedule';
import BookingForm from '../components/BookingForm';

export default class LectureRoom extends Component {

    state={
        toggleBooking:false,
    }


    onBookClickHandler=() => {
        console.log(this.state.toggleBooking);
        this.setState({
            toggleBooking:!this.state.toggleBooking
        })
    }

    render(){
        console.log('inside LR', this.props.lectureRoom);
        return (
            <div className='lecture-room'> 
                {this.props.lectureRoom.name}

                <ul>
                    {this.props.lectureRoom.lecture_schedules.map(schedule=>
                        <Schedule key={schedule.id} schedule={schedule}
                            selectedDate={this.props.selectedDate}
                        />
                        )}
                </ul>
                <button onClick={()=>this.onScheduleClickHandler()}>Schedule Event</button>
                {this.state.toggleBooking
                ? <BookingForm/>
                :null
                }
            </div>
        );
    }
};
